// App-level build.gradle for React Native 0.71.x
apply plugin: "com.android.application"
apply plugin: "com.facebook.react"

/**
 * This is the configuration block for React Native
 * Include this if you are integrating React Native into an existing app
 */
react {
    root = file("../../")  // Path to your React Native project
    entryFile = "index.js"
    enableHermes = true  // Enable for better performance
}

android {
    namespace "com.your.app"
    compileSdkVersion rootProject.ext.compileSdkVersion

    defaultConfig {
        applicationId "com.your.app"
        minSdkVersion rootProject.ext.minSdkVersion
        targetSdkVersion rootProject.ext.targetSdkVersion
        versionCode 1
        versionName "1.0"
    }

    buildTypes {
        release {
            minifyEnabled true
            proguardFiles getDefaultProguardFile("proguard-android.txt"), "proguard-rules.pro"
        }
    }

    // Configure only for Android projects using Java 8 features
    compileOptions {
        sourceCompatibility JavaVersion.VERSION_11
        targetCompatibility JavaVersion.VERSION_11
    }
}

dependencies {
    // React Native core dependencies
    implementation "com.facebook.react:react-android"
    implementation "com.facebook.react:hermes-android"

    // For animated GIF support
    implementation "com.facebook.fresco:animated-gif"

    // For WebP support
    implementation "com.facebook.fresco:webpsupport"
    implementation "com.facebook.fresco:animated-webp"

    debugImplementation("com.facebook.flipper:flipper:${FLIPPER_VERSION}")
    debugImplementation("com.facebook.flipper:flipper-network-plugin:${FLIPPER_VERSION}") {
        exclude group:'com.squareup.okhttp3', module:'okhttp'
    }

    debugImplementation("com.facebook.flipper:flipper-fresco-plugin:${FLIPPER_VERSION}")
}

apply from: file("../../node_modules/@react-native-community/cli-platform-android/native_modules.gradle"); applyNativeModulesAppBuildGradle(project)
